<script setup>
import { db } from '@/components/firebaseConfig/FirebaseConfig.js';
import FloatingConfigurator from '@/components/FloatingConfigurator.vue';
import { addDoc, collection, getDocs } from 'firebase/firestore';
import { reactive, ref } from 'vue';
import { useRouter } from 'vue-router';
// const email = ref('');
// const password = ref('');
const checked = ref(false);

const state = reactive({
    email: '',
    password: ''
});
const router = useRouter();
const loginUser = async () => {
    const docRef = await addDoc(collection(db, 'teacherlogin'), {
        name: state.name,
        price: state.price,
        url: state.image,
        quantity: state.quantity,
        description: state.description
    });
    console.log('addProducts', docRef.id);

    (state.name = ''), (state.price = 0), (state.image = ''), (state.quantity = 0), (state.description = '');

    const querySnapshot = await getDocs(collection(db, 'teacherlogin'));
    const productArray = [];
    querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        productArray.push(doc.data());
    });
    state.productData = productArray.reverse();
};

